package loops;

public class LearnForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i <= 10; i++) {
			System.out.println(5+" * "+i+" = "+ (5*i));
		}
		
		/**
		 * Infinite Loop
		 */
		for(int i=1; i <=10;i--)
			System.out.println("Testing Loops.......");
		
		
	}

}
